// DOM Elements
const sidebar = document.getElementById('sidebar');
const menuButton = document.getElementById('menu-button');
const themeToggle = document.getElementById('theme-toggle');
const mobileThemeToggle = document.getElementById('mobile-theme-toggle');
const chatInput = document.getElementById('chat-input');
const sendButton = document.getElementById('send-button');
const messagesContainer = document.getElementById('messages');
const welcomeScreen = document.getElementById('welcome-screen');
const examplePrompts = document.querySelectorAll('.example-prompt');

// Mobile menu toggle
menuButton.addEventListener('click', () => {
    sidebar.classList.toggle('active');
});

// Theme toggle
function toggleTheme() {
    const isDarkMode = document.body.getAttribute('data-theme') === 'dark';
    
    if (isDarkMode) {
        document.body.removeAttribute('data-theme');
        themeToggle.innerHTML = '<span class="icon">üåô</span> Dark Mode';
        mobileThemeToggle.innerHTML = '<span class="icon">üåô</span>';
    } else {
        document.body.setAttribute('data-theme', 'dark');
        themeToggle.innerHTML = '<span class="icon">‚òÄÔ∏è</span> Light Mode';
        mobileThemeToggle.innerHTML = '<span class="icon">‚òÄÔ∏è</span>';
    }
}

themeToggle.addEventListener('click', toggleTheme);
mobileThemeToggle.addEventListener('click', toggleTheme);

// Auto-resize chat input
chatInput.addEventListener('input', () => {
    sendButton.disabled = chatInput.value.trim() === '';
    chatInput.style.height = 'auto';
    chatInput.style.height = Math.min(chatInput.scrollHeight, 200) + 'px';
});

// Message handling
sendButton.addEventListener('click', sendMessage);
chatInput.addEventListener('keydown', (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
        e.preventDefault();
        sendMessage();
    }
});

// Example prompts
examplePrompts.forEach(prompt => {
    prompt.addEventListener('click', () => {
        const promptText = prompt.querySelector('p').textContent;
        chatInput.value = promptText;
        chatInput.dispatchEvent(new Event('input'));
        welcomeScreen.style.display = 'none';
        messagesContainer.style.display = 'block';
        addMessage(promptText, 'user');
        
        setTimeout(() => simulateTyping(), 500);
    });
});

// Conversations
document.querySelectorAll('.conversation').forEach(convo => {
    convo.addEventListener('click', () => {
        document.querySelectorAll('.conversation').forEach(c => c.classList.remove('active'));
        convo.classList.add('active');
        welcomeScreen.style.display = 'none';
        messagesContainer.style.display = 'block';
        
        if (window.innerWidth <= 768) {
            sidebar.classList.remove('active');
        }
    });
});

// New chat
document.querySelector('.new-chat-btn').addEventListener('click', () => {
    messagesContainer.innerHTML = '';
    welcomeScreen.style.display = 'flex';
    messagesContainer.style.display = 'none';
    document.querySelectorAll('.conversation').forEach(c => c.classList.remove('active'));
    
    if (window.innerWidth <= 768) {
        sidebar.classList.remove('active');
    }
});

// Message functions
function sendMessage() {
    const message = chatInput.value.trim();
    if (message) {
        welcomeScreen.style.display = 'none';
        messagesContainer.style.display = 'block';
        addMessage(message, 'user');
        chatInput.value = '';
        chatInput.style.height = 'auto';
        sendButton.disabled = true;
        
        setTimeout(() => simulateTyping(), 500);
    }
}

function addMessage(text, sender) {
    const messageElement = document.createElement('div');
    messageElement.className = `message ${sender}-message`;
    const avatarLetter = sender === 'user' ? 'U' : 'N';
    
    messageElement.innerHTML = `
        <div class="message-avatar ${sender}-avatar">${avatarLetter}</div>
        <div class="message-content">
            <div class="message-sender">${sender === 'user' ? 'You' : 'NexusAI'}</div>
            <div class="message-bubble">${text}</div>
            ${sender === 'ai' ? `
            <div class="message-actions">
                <button class="message-action">
                    <span class="icon">üëç</span> Like
                </button>
                <button class="message-action">
                    <span class="icon">üëé</span> Dislike
                </button>
                <button class="message-action">
                    <span class="icon">üìã</span> Copy
                </button>
            </div>` : ''}
        </div>
    `;
    
    messagesContainer.appendChild(messageElement);
    messageElement.scrollIntoView({ behavior: 'smooth' });
}

// Typing simulation
function simulateTyping() {
    const typingIndicator = document.createElement('div');
    typingIndicator.className = 'message ai-message';
    typingIndicator.innerHTML = `
        <div class="message-avatar ai-avatar">N</div>
        <div class="message-content">
            <div class="message-sender">NexusAI</div>
            <div class="message-bubble">
                <div class="typing-indicator">
                    <span>‚óè</span><span>‚óè</span><span>‚óè</span>
                </div>
            </div>
        </div>
    `;
    
    messagesContainer.appendChild(typingIndicator);
    typingIndicator.scrollIntoView({ behavior: 'smooth' });
    
    setTimeout(() => {
        messagesContainer.removeChild(typingIndicator);
        const responses = [
            `I'd be happy to help with that! Here's a starting point...`,
            `Here's a responsive navigation menu...`, 
            `Quantum computing is like giving a computer superpowers...`
        ];
        addMessage(responses[Math.floor(Math.random() * responses.length)], 'ai');
    }, 1500);
}

// Clipboard handling
document.addEventListener('click', function(e) {
    if (e.target.closest('.message-action')) {
        const action = e.target.closest('.message-action');
        if (action.textContent.includes('Copy')) {
            const text = action.closest('.message-content').querySelector('.message-bubble').textContent;
            navigator.clipboard.writeText(text.trim())
                .then(() => {
                    action.innerHTML = '<span class="icon">‚úì</span> Copied';
                    setTimeout(() => {
                        action.innerHTML = '<span class="icon">üìã</span> Copy';
                    }, 2000);
                });
        }
    }
});
